const startBtn=document.getElementById("start"),screens=document.querySelectorAll(".screen"),timeList=document.getElementById("time-list"),timeEl=document.getElementById("time"),board=document.getElementById("board");let time=0,score=0;function startGame(){setInterval(decreaseTime,1e3),createRandomCircle(),setTime(time)}function decreaseTime(){if(0===time)finishGame();else{let e=--time;e<10&&(e=`0${e}`),setTime(e)}}function setTime(e){timeEl.innerHTML=`00:${e}`}function finishGame(){timeEl.parentNode.classList.add("hide"),board.innerHTML=`<h1>Your score is <span class="primary">${score}</span></h1>`,setTimeout(()=>{location.reload()},3e3)}function createRandomCircle(){const e=document.createElement("div");e.classList.add("circle");const t=getRandomNumber(5,15),n=`rgb(${getRandomNumber(30,220)}, ${getRandomNumber(30,220)}, ${getRandomNumber(30,220)})`,{width:a,height:i}=board.getBoundingClientRect(),s=getRandomNumber(15,a-t-10),r=getRandomNumber(15,i-t-10);e.style.background=n,e.style.width=`${t}px`,e.style.height=`${t}px`,e.style.left=`${s}px`,e.style.top=`${r}px`,board.append(e)}function getRandomNumber(e,t){return Math.round(Math.random()*(t-e)+e)}startBtn.addEventListener("click",e=>{e.preventDefault(),screens[0].classList.add("up")}),timeList.addEventListener("click",e=>{e.target.classList.contains("time-btn")&&(time=Number(e.target.dataset.time),screens[1].classList.add("up"),startGame())}),board.addEventListener("click",e=>{e.target.classList.contains("circle")&&(score++,e.target.remove(),createRandomCircle())});